import React, { useState, useEffect } from 'react';
import { useHistory, NavLink } from 'react-router-dom';
import styled from 'styled-components';

export default function Join({ currentUser, setCurrentUser }) {

    // States
    const [login, setLogin] = useState({ name: "", password: "", room: "General" })
    const [roomArray, setRoomArray] = useState([]);
    const [errors, setErrors] = useState([]);

    // Router hooks
    const history = useHistory();

    // Fetches a list of rooms
    // Returns dropdown select options
    useEffect(() => {
    fetch('http://localhost:3000/rooms')
        .then(res => res.json())
        .then(data => setRoomArray(data))
    }, []);

    const listRooms = roomArray.map((room) => {
        return <option key={room.id} value={room.name}>{room.name}</option>
    });

    // Login handler
    const handleLogin = (e) => {
        e.preventDefault();
        fetch('http://localhost:3000/login', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(login),
        })
        .then(res => {
            if (res.ok) {
                return res.json();
            } else {
                return res.json().then((data) => {
                    throw data;
                })
            }
          })
        .then(data => { 
            setCurrentUser(data.user);
            localStorage.setItem("token", data.token);
            history.push(`/chat?name=${login.name}&room=${login.room}`);
            setLogin({
                name: "",
                password: "",
                room: "General"
            })
        })
        .catch(data => {
            setErrors(data.errors);
        })
    };

    // Signup handler

    return (
        <JoinStyled>
        <div className="scene">
            <div className="card">
                <div className="login">
                    <div className="outer">
                        <div className="inner">
                            <h1> Let's Chat </h1>
                            <form onSubmit={handleLogin}>
                            <input 
                                placeholder="Name" 
                                className="input"
                                name="name"
                                type="text"
                                value={login.name}
                                onChange={(e) => setLogin({
                                    ...login, [e.target.name]: e.target.value})}
                            />

                            <input 
                                placeholder="Password" 
                                className="input"
                                name="password"
                                type="password"
                                value={login.password}
                                onChange={(e) => setLogin({
                                    ...login, [e.target.name]: e.target.value})}
                            />

                            <select 
                                className="select"
                                name="room"
                                value={login.room}
                                onChange={(e) => setLogin({
                                    ...login, [e.target.name]: e.target.value})}
                            >
                            {listRooms}
                            </select>

                            {errors.map((error) => (
                            <p key={error} style={{ color: "red" }}>
                                {error}
                            </p>))}

                            <button 
                                className="button mt-20" 
                                type="submit"
                            >
                            SIGN IN
                            </button>
                            </form>

                            <button
                                className="button mt-20"
                                style={{background: "#16D400"}}
                                type="submit"
                            >
                            SIGN UP
                            </button>

                        </div>
                    </div>
                </div>

                <div className="signup">

                </div>
            </div>
        </div>

        </JoinStyled>
    )
}

// CSS
const JoinStyled = styled.div`
* {
    box-sizing: border-box;
}

.scene {
    height: 100vh;
    width: 150vh;
    perspective: 1000px;
}

.outer {
    position: absolute;
    left: 40%;
    display: flex;
    justifiy-content: center;
    text-align: center;
    height: 100vh;
    width: 150vh;
    align-items: center;
    background-color: #1A1A1D;
}

.inner {
    width: 25%;
}

.input {
    margin-top: 5px;
    padding: 15px 15px;
    width: 100%;
}

.select {
    margin-top: 5px;
    width: 100%;
    padding: 15px 15px;
}

h1 {
    color: #FFF;
    font-size: 2.5rem;
    padding-bottom: 10px;
    border-bottom: 2px solid #FFF;
}

button {
    margin-top: 20px;
    color: #FFF !important;
    background: #2979FF;
    padding: 20px;
    border-radius: 10px;
    display: inline-block;
    border: none;
    width: 100%;
}

button:hover {
    background: #6ba2ff;
}

@media (min-width: 320px) and (max-width: 480px) {
    .outer {
        height: 100%;
        width: 100%;
    }
}
`
