import React, { useState, useEffect } from 'react';
import ActionCable from 'actioncable';
import io from 'socket.io-client';
import Input from './Input';
import Message from './Message';
import styled from 'styled-components';

export default function Chat({ currentUser }) {

    // States & variables
    const [chats, setChats] = useState(null);
    const [name, setName] = useState("");
    const [message, setMessage] = useState("");
    const [messageList, setMessageList] = useState([]);
    const socket = io('http://localhost:4000/')
    const room = (new URLSearchParams(window.location.search)).get('room');

    // Listens on socket
    useEffect(() => {
      createSocket();
    }, [])

    // Create Action Cable socket
    const createSocket = () => {
      let cable = ActionCable.createConsumer('http://localhost:3000/cable');
      setChats(cable.subscriptions.create({
        channel: 'message_channel.rb'
      }, {
      connected: () => {},
      received: (data) => { 
        console.log(data);
      },
      create: function(chatContent) {
        this.perform('create', {
          content: chatContent
        });
      }
    })
    )
  }

    // Handlers
    const sendMessage = (e) => {
      e.preventDefault();
      chats.create(message);
      setMessage('')
    };

    return (
        <ChatStyled>
        <h1 className="room-title">{room}</h1>
        <div className="container">
            {messageList.length === 0 ? null :  <Message currentUser={currentUser} message={message} messageList={messageList}/> }
            <Input message={message} setMessage={setMessage} sendMessage={sendMessage} />
        </div>
        </ChatStyled>
    )
}

