import React, { useState } from 'react';
import styled from 'styled-components';

export default function Signup() {
    const [login, setLogin] = useState({name: "", password: "", room: "General"})
    const [front, setFront] = useState(true);

    const flip = (e) => {
        setFront(!front);
        console.log(e.target);
    }

    return (
        <SignupStyled>
            <div className="scene scene--card">
                <div onClick={flip} className={`card ${front ? "is-flipped" : ""}`}>
                    <div className="card__face card__face--front"><div className="outer">
                        <div className="inner">
                            <h1> Let's Chat </h1>
                            <form onSubmit={handleLogin}>
                            <input 
                                placeholder="Name" 
                                className="input"
                                name="name"
                                type="text"
                                value={login.name}
                                onChange={(e) => setLogin({
                                    ...login, [e.target.name]: e.target.value})}
                            />

                            <input 
                                placeholder="Password" 
                                className="input"
                                name="password"
                                type="password"
                                value={login.password}
                                onChange={(e) => setLogin({
                                    ...login, [e.target.name]: e.target.value})}
                            />

                            <select 
                                className="select"
                                name="room"
                                value={login.room}
                                onChange={(e) => setLogin({
                                    ...login, [e.target.name]: e.target.value})}
                            >
                            {listRooms}
                            </select>

                            {errors.map((error) => (
                            <p key={error} style={{ color: "red" }}>
                                {error}
                            </p>))}

                            <button 
                                className="button mt-20" 
                                type="submit"
                            >
                            SIGN IN
                            </button>
                            </form>
                        </div>
                    </div></div>
                    <div className="card__face card__face--back">back</div>
                </div>
            </div>
        </SignupStyled>
    )
}

const SignupStyled = styled.div`
.card {
  width: 100%;
  height: 100%;
  transition: transform 1s;
  transform-style: preserve-3d;
  cursor: pointer;
  position: relative;
}

.card.is-flipped {
  transform: rotateY(180deg);
}

.card__face {
  position: absolute;
  width: 100%;
  height: 100%;
  line-height: 260px;
  color: white;
  text-align: center;
  font-weight: bold;
  font-size: 40px;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
}

.card__face--front {
  background: red;
}

.card__face--back {
  background: blue;
  transform: rotateY(180deg);
}

`
