import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

export default function Join() {
    const [name, setName] = useState("");
    const [room, setRoom] = useState("");

    return (
        <JoinStyled>
        <div className="outer">
            <div className="inner">
                <header className="heading"> Join </header>

                <input 
                    placeholder="Name" 
                    className="input" 
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />

                <input 
                    placeholder="Room" 
                    className="input" 
                    type="text"
                    value={room}
                    onChange={(e) => setRoom(e.target.value)}
                />

                <Link to={`/${room}`}>
                    <button className="button mt-20" type="submit">SIGN IN</button>
                </Link>

            </div>
        </div>
        </JoinStyled>
    )
}

// CSS
const JoinStyled = styled.div`
* {
    box-sizing: border-box;
}

.outer {
    margin: auto;
    width: 50%;
    display: flex;
    justifiy-content: center;
    text-align: center;
    height: 100vh;
    align-items: center;
    background-color: #1A1A1D;
}

.inner {
    width: 20%;
}

.input {
    margin-top: 5px;
    padding: 15px 15px;
    width: 100%;
}

header {
    color: #FFF;
    font-size: 2.5rem;
    padding-bottom: 10px;
    border-bottom: 2px solid #FFF;
}

button {
    margin-top: 20px;
    color: #FFF !important;
    background: #2979FF;
    padding: 20px;
    border-radius: 10px;
    display: inline-block;
    border: none;
    width: 100%;
}

button:hover {
    background: #6ba2ff;
}

@media (min-width: 320px) and (max-width: 480px) {
    .outer {
        height: 100%;
        width: 100%;
    }
}
`
