const ChatStyled = styled.div`
.room-title {
  text-transform: capitalize;
  font-size: 36px;
  color: #FFF;
  border-bottom: 2px #FFF solid;
}

.container {
  position: relative;
  margin: auto;
  height: 500px;
  width: 70%;
  background-color: white;
}

`
