import React from 'react';
import styled from 'styled-components';

export default function Message({ message, messageList }) {

    // useEffect to grab Chatroom messages
    // Display messages as mapped product in JSX
    const displayMessage = messageList.map((message, index) => {
        return <p key={index} className={`text ${message.currentUser ? "my-message" : "received-message"}`}> {message} </p>
    })

    return (
        <MessageStyle>
        <div className="container">
            <div className="box">
                {displayMessage}
            </div>
        </div>
        </MessageStyle>
    )
}

// CSS

const MessageStyle = styled.div`
.container {
    display: flex;
    width: 20%;
}

.box {
    position: relative;
    justify-content: flex-end;
    align-items: center;
    padding: 10px;
    margin: 10px 0;
}

.text {
    display: inline-block;
    position: relative;
    flex: auto;
    overflow: auto;
    color: black;
    margin-left: 50px;
    background-image: linear-gradient(#99c2ff, #0066ff);
    padding: 10px 20px;
    font-size: 25px;
    border-radius: 10px;
    width: 100%;
    letter-spacing: 0;
    float: left;
    font-size: 1.5em;
    word-wrap: break-word;
}

`
