import React from 'react';
import styled from 'styled-components';

export default function Signup() {
    return (
        <SignupStyled>
        <div className="scene">
            <div className="card">
                <div className="card__face card__face--front">front</div>
                <div className="card__face card__face--back">back</div>
            </div>
        </div>
        </SignupStyled>
    )
}

const SignupStyled = styled.div`
.scene {
  width: 200px;
  height: 260px;
  border: 1px solid #CCC;
  margin: 40px 0;
  perspective: 600px;
}

.card {
  width: 100%;
  height: 100%;
  transition: transform 1s;
  transform-style: preserve-3d;
  cursor: pointer;
  position: relative;
}

.card.is-flipped {
  transform: rotateY(180deg);
}

.card__face {
  position: absolute;
  width: 100%;
  height: 100%;
  line-height: 260px;
  color: white;
  text-align: center;
  font-weight: bold;
  font-size: 40px;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
}

.card__face--front {
  background: red;
}

.card__face--back {
  background: blue;
  transform: rotateY(180deg);
}
`
