import React, { useState } from 'react';
import styled from 'styled-components';

export default function Signup() {
    const [front, setFront] = useState(true);

    const flip = (e) => {
        setFront(!front);
        console.log(e.target);
    }

    return (
        <SignupStyled>
            <div className="scene scene--card">
                <div className={`card ${front ? "is-flipped" : ""}`}>
                    <div className="card__face card__face--front">
                        <div className="outer">
                            <div className="inner">
                                <h1> Let's Chat </h1>
                                <form>
                                <input 
                                    placeholder="Name" 
                                    className="input"
                                    name="name"
                                    type="text"
                                />

                                <input 
                                    placeholder="Password" 
                                    className="input"
                                    name="password"
                                    type="password"
                                />


                                <button 
                                    onClick={flip}
                                    className="button mt-20" 
                                    type="submit"
                                >
                                SIGN IN
                                </button>
                                </form>
                            </div>
                        </div>
                    </div>


                    <div onClick={flip} className="card__face card__face--back">back</div>
                </div>
            </div>
        </SignupStyled>
    )
}

const SignupStyled = styled.div`
.card {
  transition: transform 1s;
  transform-style: preserve-3d;
  cursor: pointer;
}

.card.is-flipped {
  transform: rotateY(180deg);
}

.card__face {
    position: absolute;
    left: 40%;
    height: 150vh;
    display: flex;
    justifiy-content: center;
    text-align: center;
    align-items: center;
    color: white;
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
}

.card__face--front {
}

.card__face--back {
  transform: rotateY(180deg);
}

* {
    box-sizing: border-box;
}

h1 {
    font-size: 2.5rem;
    padding-bottom: 10px;
    border-bottom: 2px solid #FFF;
}

.input {
    margin-top: 5px;
    padding: 15px 15px;
    width: 100%;
}

.outer {
    position: absolute;
    display: flex;
    justifiy-content: center;
    text-align: center;
    align-items: center;
    width: 50vh;
    height: 100vh;
}




`
