import React from 'react';
import styled from 'styled-components';

export default function Join() {
    return (
        <JoinStyled>
        <div className="outer">
            <div className="inner">
                <header className="heading"> Join </header>

                <input 
                    placeholder="Name" 
                    className="input" 
                    type="text" 
                /> 


                <input 
                    placeholder="Room" 
                    className="input" 
                    type="text" 
                /> 

                <button className="button mt-20" type="submit">SIGN IN</button>
            </div>
        </div>
        </JoinStyled>
    )
}

// CSS
const JoinStyled = styled.div`
* {
    box-sizing: border-box;
}

.outer {
    padding: 1000px;
    display: flex;
    margin: center;
    justifiy-content: center;
    text-align: center;
    height: 100vh;
    width: 200vh;
    align-items: center;
    background-color: #1A1A1D;
}

.inner {
    width: 20%;
}

.input {
    margin-top: 5px;
    padding: 15px 15px;
    width: 100%;
}

header {
    color: #FFF;
    font-size: 2.5rem;
    padding-bottom: 10px;
    border-bottom: 2px solid #FFF;
}

button {
    margin-top: 20px;
    color: #FFF !important;
    background: #2979FF;
    padding: 20px;
    border-radius: 10px;
    display: inline-block;
    border: none;
    width: 100%;
}

button:hover {
    background: #6ba2ff;
}

@media (min-width: 320px) and (max-width: 480px) {
    .outer {
        height: 100%;
        width: 100%;
    }
}
`
