import React from 'react'

export default function Input({ message, setMessage, handleSendMessage }) {
    console.log(message)

    return (
    <form className="form">
        <input
            className="input"
            type="text"
            placeholder="Type a message"
            value={message}
            onClick={(e) => setMessage(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' ? setMessage(e.target.value) : null}
        />
        <button className="sendButton" onClick={(e) => handleSendMessage(e)}> Send </button>
    </form>
    )
}
