import React from 'react';
import styled from 'styled-components';

export default function Message({ message, messages }) {
    return (
        <MessageStyle>
        <div className="messages-container justifyEnd">
            <div className="message-box">
                {messages.map((message, i) => (
                <div key={i} className={`message-text ${message.currentUser ? "my-message" : "received-message"}`}>
                {message.body}
                </div>
                ))}
            </div>
        </div>
        </MessageStyle>
    )
}

const MessageStyle = styled.div`
.message-container {
    display: flex;
    justify-content: flex-end;
    padding: 0 5%;
    margin-top: 3px;
}

.message-box {
    background: #F3F3F3;
    border-radius: 20px;
    padding: 5px 20px;
    color: white;
    display: inline-block;
    max-width: 80%;
}

.message-text {
    color: black;
    width: 100%;
    letter-spacing: 0;
    float: left;
    font-size: 1.5em;
    word-wrap: break-word;
}

.message-list {
    position: fixed;
    bottom: 0;
}

li {
    padding: 0;
    list-style-type: none;
}
`
